<?php/** * @package  PostSlider * @developer  name : Abu Sayed Russell */namespace MMWPS\Common;if (!class_exists('MMWPS_ActivatePostSlider')) {	class MMWPS_ActivatePostSlider {		public static function ActivatePostSliderFlush() {			flush_rewrite_rules();			$default = array();			if ( ! get_option( 'mmwps_version' ) ) {				update_option( 'mmwps_version', MMWPS_VERSION );			}			if ( ! get_option( 'mmwps_post_slider' ) ) {				update_option( 'mmwps_post_slider', 'last_code_russell' );			}		}	}}