<?php/** * @package  PostSlider * @developer  name : Abu Sayed Russell */namespace MMWPS\Common;if (!class_exists('MMWPS_BaseController')) {		class MMWPS_BaseController {				public function mmwps_last_code()		{			$option = get_option( 'mmwps_post_slider' );			return isset( $option ) && $option == 'last_code_russell' ? true : false;		}		public function mmwps_wp_version_check() {			$wp_version = get_bloginfo( 'version' );			return ! version_compare( $wp_version, '4.9', '<' ) ? true : false;		} 	}}