<?php/** * @package  PostSlider * @developer  name : Abu Sayed Russell */namespace MMWPS\Common;if (!class_exists('MMWPS_CommonController')) {		class MMWPS_CommonController{				public function mmwps_register()		{			add_filter( "plugin_action_links_" . MMWPS_POST_SLIDER_PLUGIN_BASE, array( $this, 'mmwps_settings_link' ) );			add_action( 'wp_head', array( $this, 'mmwps_developer' ) );			add_filter('the_generator', array( $this,'mmwps_remove_version'));			add_action( 'admin_notices', array( $this, 'mmwps_wp_version_error_warning'));		}		/**		 * Setting Link		 * Feature added by : Abu Sayed Russell abusayedrussell@gmail.com		 * Date       : 15.04.2020		*/		public function mmwps_settings_link( $links )		{			$settings_link = '<a href="admin.php?page=mmwps-slider">Settings</a>';			array_push( $links, $settings_link );			return $links;		}		/**		 * WP version check		 * Feature added by : Abu Sayed Russell abusayedrussell@gmail.com		 * Date       : 15.04.2020		*/		public function mmwps_wp_version_error_warning( ) {			$wp_version = get_bloginfo( 'version' );			if ( ! version_compare( $wp_version, MMWPS_WPMIN_VERSION, '<' ) ) {				return;			}		?>		<div class="notice notice-warning mmwps-warning">		<p><?php			echo sprintf(				__( '<strong>Post slider %1$s requires WordPress %2$s or higher to work properly.</strong> Please <a href="%3$s">update WordPress</a> first.', 'posr-slider' ),				MMWPS_VERSION,				MMWPS_WPMIN_VERSION,				admin_url( 'update-core.php' )			);		?></p>		</div>		<?php }		/**		 * Developer information		 * Feature added by : Abu Sayed Russell abusayedrussell@gmail.com		 * Date       : 15.04.2020		*/		public function mmwps_developer()		{			echo '<!-- Post Slider Developed by Abu Sayed Russell Email: abusayedrussell@gmail.com -->';		}		/**		 * Remove version		 * Feature added by : Abu Sayed Russell abusayedrussell@gmail.com		 * Date       : 15.04.2020		*/		public function mmwps_remove_version() {			return '';		}	}}