<div id="wrapper" class="mmwps-wrapper">  <div class="content">    <div class="row">        <div class="col-md-12">            <div class="panel_s">                 <div class="panel-body">                <div class="_button">                <span class="top-heading  mright5 test pull-left display-block"><?php esc_html_e( 'Sliders', 'post-slider' ); ?></span>                <a href="admin.php?page=mmwps-new-slider" class="top-heading mright5 test pull-right display-block" data-toggle="tooltip" data-placement="top" title="Add Slider"><i class="fa fa-plus"></i></a>              </div>              </div>              <div class="panel-body">                <table id="mmwpsTable" class="table">                    <thead>                        <tr>                            <th width="10"><?php esc_html_e( '#', 'post-slider' ); ?></th>                            <th><?php esc_html_e( 'Shortcode', 'post-slider' ); ?></th>                            <th><?php esc_html_e( 'Name', 'post-slider' ); ?></th>                            <th><?php esc_html_e( 'Type', 'post-slider' ); ?></th>                            <th><?php esc_html_e( 'Status', 'post-slider' ); ?></th>                            <th><?php esc_html_e( 'Addby', 'post-slider' ); ?></th>                            <th><?php esc_html_e( 'Created', 'post-slider' ); ?></th>                            <th><?php esc_html_e( 'Option', 'post-slider' ); ?></th>                        </tr>                    </thead>                     <tbody>                         <?php $sliders = mmwps_get_sliders(); ?>                        <?php if (isset( $sliders ) && count( $sliders ) > 0 ): ?>                            <?php $i = 1; foreach ($sliders as $slider):                               $shortcode_title = strtolower(MMWPS_PREFIX.$slider->_title);                              $shortcode = str_replace(' ', '_', $shortcode_title);                             ?>                                <tr data-id="<?php echo intval($slider->_id); ?>" data-name="<?php echo esc_html( str_replace(' ', '_', $slider->_title) ); ?>" id="<?php echo intval($slider->_id); ?>">                                    <td><?php echo $i; ?></td>                                    <td><?php echo esc_attr( '['.$shortcode.']' ); ?></td>                                    <td><?php echo esc_html__( $slider->_title, 'post-slider' ); ?></td>                                    <td><?php echo esc_html__( $slider->_type, 'post-slider' ); ?></td>                                     <?php                                           $checked = $slider->_slider_status == 1 ? 'checked' : '';                                          $checked_class = $slider->_slider_status == 1 ? 'slider_deactive' : 'slider_active';                                      ?>                                    <td>                                        <div class="onoffswitch onoffswitchstatus">                                          <input style="display: none;" type="checkbox" data-switch-url="#" name="onoffswitch" class="onoffswitch-checkbox <?php echo sanitize_html_class($checked_class); ?>" id="rk_<?php echo intval($slider->_id); ?>" data-id="<?php echo intval($slider->_id); ?>" data-nonce="<?php echo wp_create_nonce('slider_status_nonce'); ?>" <?php echo $checked; ?>>                                          <label class="onoffswitch-label" data-toggle="tooltip" data-placement="top" data-title="Change Status" data-original-title="Change Status" title="Change Status" for="rk_<?php echo intval($slider->_id); ?>"></label>                                      </div>                                    </td>                                      <td>                                        <span data-user-id="<?php echo intval( $slider->_auth_id ); ?>" data-user-role="<?php echo esc_html__( mmwps_userrole($slider->_auth_id), 'post-slider' ); ?>" data-user-email="<?php echo esc_html__( mmwps_auth_email($slider->_auth_id), 'post-slider' ); ?>" data-toggle="tooltip" data-placement="top" data-title="<?php echo esc_html__('Added By '. mmwps_get_username($slider->_auth_id), 'post-slider' ); ?>" data-original-title="<?php echo esc_html__('Added By'. mmwps_get_username($slider->_auth_id), 'post-slider' ); ?>" title="<?php echo esc_html__( 'Added By '.mmwps_get_username($slider->_auth_id), 'post-slider' ); ?>"><?php echo esc_html__( mmwps_get_username($slider->_auth_id), 'post-slider' ); ?></span></td>                                    <td title="<?php echo esc_html__($slider->_created_at, 'post-slider'); ?>" ><?php echo esc_html__( mmwps_create_before( $slider->_created_at ), 'post-slider' ); ?>                                    </td>                                                            <td>                                     <a href="admin.php?page=mmwps-edit-slider&edit-slider=<?php echo $slider->_slug; ?>" data-toggle="tooltip" data-placement="top" data-title="Edit" data-original-title="Edit" title="Edit" data-id="<?php echo intval($slider->_id); ?>" data-nonce="<?php echo wp_create_nonce('slider_edit_nonce'); ?>" class="btn btn-default btn-icon menu_edit" title="Edit"><i class="fa fa-edit"></i></a>                                                      <a href="<?php echo esc_js( 'javascript:void(0)' ); ?>" data-id="<?php echo intval($slider->_id); ?>" data-nonce="<?php echo wp_create_nonce('delete_slider_nonce') ?>" class="btn btn-danger slider_delete btn-icon" data-toggle="tooltip" data-placement="top" data-title="Permanently Remove" data-original-title="Permanently Remove" title="Permanently Remove"><i class="fa fa-remove"></i></a>                                  </td>                                </tr>                            <?php $i++; endforeach; ?>                        <?php endif; ?>                    </tbody>                </table>              </div>               </div>        </div>    </div>  </div></div>